package com.amir.steelsenseandroidtask.uiimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.widthimport androidx.compose.material3.Switchimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dp@Composablefun ChartToggleRow(    showLine1: Boolean,    showLine2: Boolean,    showLine3: Boolean,    onToggle: (Int) -> Unit) {    Row(        modifier = Modifier            .padding(50.dp)            .fillMaxWidth(),        horizontalArrangement = Arrangement.SpaceEvenly,        verticalAlignment = Alignment.CenterVertically    ) {        ChartToggle("خط A", showLine1) { onToggle(1) }        ChartToggle("خط B", showLine2) { onToggle(2) }        ChartToggle("خط C", showLine3) { onToggle(3) }    }}@Composablefun ChartToggle(label: String, checked: Boolean, onToggle: () -> Unit) {    Row(verticalAlignment = Alignment.CenterVertically) {        Text(label)        Switch(checked = checked, onCheckedChange = { onToggle() })    }}