package com.amir.steelsenseandroidtask.uiimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.Switchimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dp@Composablefun SwitchWithLabel(    label: String,    checked: Boolean,    onToggle: (Boolean) -> Unit) {    Row(        modifier = Modifier.padding(9.dp),        verticalAlignment = Alignment.CenterVertically    ) {        Text(label, modifier = Modifier.padding(end = 8.dp))        Switch(checked = checked, onCheckedChange = onToggle)    }}